exec < marks1 rm result head="name \t marks \t \t  \t \tdivision" echo -e $head > result while read line do   flag=0   sum=0   for x in $line   do     if [ $flag -eq 0 ]     then       echo -n $x >> result       flag=1     else       sum=`expr $sum + $x`       echo -ne "\t $x" >> result     fi   done   per=`echo $sum / 4 | bc`   if [ $per -lt 40 ]   then     div="fail"   elif [ $per -ge 40 -a $per -le 49 ]   then     div=3   elif [ $per -ge 50 -a $per -le 99 ]   then     div=1   fi   echo -e "\t $div" >> result done cat result      
