echo "Enter 3 values of a,b,c" read a b c if [ $a -eq 0 ] then   echo "Root cannot be defined"   exit fi disc=`expr $b \* $b -4 \* $a \* $c` disc1=`echo -e "scale=6 \n sqrt($disc)" | bc` disc2=`expr 2 \* $a` b=`expr $b \* -1` if [ $disc -gt 0 ] then   echo "Roots are unequal"   x1=`echo -e "scale=6 \n $b + $disc1" | bc`   root1=`echo -e "scale=6 \n $x1 / $disc2" | bc`   x2=`echo -e "scale=6 \n $b - $disc1" | bc`   root2=`echo -e "scale=6 \n $x2 / $disc2" |bc`   echo "root1=" $root1   echo "root2=" $root2 elif [$disc -le 0 ] then   echo "Roots are imaginary" else   echo "Roots are equal"   root=`echo -e "scale=6 \n - $b / $disc2" | bc`   echo "The root=" $root fi   
